export default function filter(array, options) {
  const { textProperty = "", valueProperty } = options;

  let path = textProperty ? textProperty.split(".") : [];
  let result = path.reduce((a, v) => a[v], array);
  console.log(result, path);

  const filtered = options
    ? array.filter((element) => element[textProperty] === valueProperty)
    : array;

