import _ from "lodash";

function filter(array = [], options = {}) {
  const { textProperty = "", valueProperty = "" } = options;

  let newarray = array.filter(
    (item) => _.get(item, textProperty) === valueProperty
  );

  return newarray;
}

filter.defaultProps = {};

export default filter;
